css:
.badge {
  display: inline-block;
  line-height: 1;
  vertical-align: middle;
  font-weight: normal;
  text-align: center;
  white-space: nowrap;
  border-radius: 1em;
  color: #282f3e;
  background-color: rgba(112, 122, 143, 0.18);
  font-size: 11px;
  min-width: 17px;
  padding: 3px 5px;
}
.badge:empty {
  display: none;
}
.badge.badge-info {
  color: #ffffff;
  background-color: #1f96ff;
}
.badge.badge-success {
  color: #ffffff;
  background-color: #40b557;
}
.badge.badge-warning {
  color: #ffffff;
  background-color: #ce9d3b;
}
.badge.badge-error {
  color: #ffffff;
  background-color: #d24b4b;
}
.badge.badge-large {
  font-size: 18px;
  min-width: 28px;
  padding: 5px 8px;
}
.badge.badge-medium {
  font-size: 14px;
  min-width: 22px;
  padding: 4px 6px;
}
.badge.badge-small {
  font-size: 10px;
  min-width: 16px;
  padding: 3px 5px;
}
.badge.badge-flexible {
  font-size: 0.8em;
  min-width: 1.6em;
  padding: 0.4em 0.6em;
}
.badge.icon {
  font-size: 13px;
  padding: 5px 10px;
}
