(function() {
  describe("Language C# package", function() {
    beforeEach(function() {
      return waitsForPromise(function() {
        return atom.packages.activatePackage("language-csharp");
      });
    });
    describe("C# grammar", function() {
      return it("parses the grammar", function() {
        var grammar;
        grammar = atom.grammars.grammarForScopeName("source.cs");
        expect(grammar).toBeDefined();
        return expect(grammar.scopeName).toBe("source.cs");
      });
    });
    describe("C# Script grammar", function() {
      return it("parses the grammar", function() {
        var grammar;
        grammar = atom.grammars.grammarForScopeName("source.csx");
        expect(grammar).toBeDefined();
        return expect(grammar.scopeName).toBe("source.csx");
      });
    });
    return describe("NAnt Build File grammar", function() {
      return it("parses the grammar", function() {
        var grammar;
        grammar = atom.grammars.grammarForScopeName("source.nant-build");
        expect(grammar).toBeDefined();
        return expect(grammar.scopeName).toBe("source.nant-build");
      });
    });
  });

}).call(this);
